import React from 'react';

import { UsuarioService } from '../../services/usuario.service';

class LoginPage extends React.Component {

    constructor(props) {
        super(props);

        UsuarioService.logout();

        this.state = {
            username: '',
            password: '',
            submitted: false,
            loading: false,
            error: ''
        };
        
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password, returnUrl } = this.state;

        if (!(username && password)) {
            return;
        }

        this.setState({ loading: true });
        UsuarioService.login(username, password)
            .then(
                user => {
                    let path = '/';
                    if (user.perfil === 'ADMINISTRADOR') {
                        path = '/admin';
                    } else if (user.perfil === 'TRIADOR') {
                        path = '/triagem';
                    } else if (user.perfil === 'FINALIZADOR') {
                        path = '/finalizacao';
                    }
                    const { from } = this.props.location.state || { from: { pathname: path } };
                    this.props.history.push(from);
                },
                error => this.setState({ error, loading: false })
            );
    }

    render() {
        const { username, password, submitted, loading, error } = this.state;
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>Login</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <label htmlFor="username">Nome de Usuário</label>
                        <input type="text" className="form-control" name="username" value={username} onChange={this.handleChange} />
                        {submitted && !username && 
                            <div className="help-block">O nome de usuário deve ser informado</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Senha</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">A senha deve ser informada</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary" disabled={loading}>Login</button>
                        {loading &&
                            <img src="data:image/gif;base64," />
                        }
                    </div>
                    {error &&
                        <div className={'alert alert-danger'}>{error}</div>
                    }
                </form>
            </div>
        );
    }
}

export { LoginPage }; 